"use strict";

$(document).ready(function() {
    var e = AmCharts.makeChart("sales-analytics", {
        type: "serial",
        theme: "light",
        marginRight: 15,
        marginLeft: 15,
        autoMarginOffset: 20,
        dataDateFormat: "YYYY-MM-DD",
        valueAxes: [{
            id: "v1",
            axisAlpha: 0,
            position: "left",
            ignoreAxisWidth: !0
        }],
        balloon: {
            borderThickness: 1,
            shadowAlpha: 0
        },
        graphs: [{
            id: "g1",
            balloon: {
                drop: !0,
                adjustBorderColor: !1,
                color: "#ffffff",
                type: "smoothedLine"
            },
            fillAlphas: .3,
            bullet: "round",
            bulletBorderAlpha: 1,
            bulletColor: "#FFFFFF",
            lineColor: "#9ccc65",
            bulletSize: 5,
            hideBulletsCount: 50,
            lineThickness: 3,
            title: "red line",
            useLineColorForBulletBorder: !0,
            valueField: "value",
            balloonText: "<span style='font-size:18px;'>[[value]]</span>"
        }],
        chartCursor: {
            valueLineEnabled: !0,
            valueLineBalloonEnabled: !0,
            cursorAlpha: 0,
            zoomable: !1,
            valueZoomable: !0,
            valueLineAlpha: .5
        },
        chartScrollbar: {
            autoGridCount: !0,
            graph: "g1",
            oppositeAxis: !0,
            scrollbarHeight: 40
        },
        categoryField: "date",
        categoryAxis: {
            parseDates: !0,
            dashLength: 1,
            minorGridEnabled: !0
        },
        export: {
            enabled: !0
        },
        dataProvider: [{
            date: "2012-07-27",
            value: 13
        }, {
            date: "2012-07-28",
            value: 11
        },  {
            date: "2013-01-30",
            value: 81
        }]
    });
    setTimeout(function() {
        e.zoomToIndexes(Math.round(.45 * e.dataProvider.length), Math.round(.6 * e.dataProvider.length))
    }, 800);
 
  $(window).on("resize", function() {
        floatchart()
    }), $("#mobile-collapse").on("click", function() {
        setTimeout(function() {
            floatchart()
        }, 700)
    })
});;